import Head from "next/head";
import { useEffect } from "react";

import {
  getPhotoData,
  getKeywordsForPhoto,
} from "../../server/view-data-service";
import { serverToClient } from "../../server/photos/photo-data-conversion";
import buildSrcSet from "../../client/src/photos/src-set-builder";

function PhotoPage({ keywords, selectedPhoto }) {
  let availWidth = 400;
  useEffect(() => {
    availWidth = screen.availWidth;
  });

  selectedPhoto.srcSet = buildSrcSet(photo.sizes, availWidth);

  return (
    <>
      <Head>
        <meta name="keywords" content={keywords.join(", ")} />
      </Head>

      <div id="container">
        <PhotosWrapper selectedPhoto={selectedPhoto} />
        />
      </div>
    </>
  );
}

export async function getStaticPaths() {
  const allPhotos = await getPhotoData();
  return {
    paths: allPhotos.map((photo) => ({
      params: {
        id: photo.key,
      },
    })),
  };
}

export async function getStaticProps(context) {
  return Promise.all([getPhotoData(), getKeywordsForPhoto()]).then(
    ([photoData, photoKeywords]) => {
      const selectedPhoto = photoData.photos.find(
        (photo) => photo.key === context.params.id
      );

      if (!selectedPhoto) {
        return {
          notFound: true,
        };
      }

      const mappedPhoto = serverToClient(selectedPhoto, photoData.base);
      return {
        props: {
          keywords: photoKeywords,
          photo: mappedPhoto,
        },
      };
    }
  );
}

export default PhotoPage;
